class Solution {
    vector<int> dfs(int node, int parent, vector<vector<int>>& alist, vector<int>& ans, string &label){
        // count vector for storing each letter count from sub-tree
        vector<int> count(26, 0);

        // populating count vector
        for(auto nbr : alist[node]){
            if(nbr != parent){
                vector<int> nbrCount = dfs(nbr, node, alist, ans, label);
                for(int i=0 ; i<26 ; i++)
                    count[i] += nbrCount[i];
            }
        }

        // updating count vector and answer vector
        char currChar = label[node];
        count[currChar - 'a']++;
        ans[node] = count[currChar - 'a'];

        // returning the count vector to its parent
        return count;
    }
public:
    vector<int> countSubTrees(int n, vector<vector<int>>& edges, string labels) {
        //declaring adjacency list and answer vector
        vector<vector<int>> alist(n, vector<int>());
