        for(auto edge : edges){
            alist[edge[0]].push_back(edge[1]);
            alist[edge[1]].push_back(edge[0]);
        }

        // Calling the DFS function on node 0
        // dfs(currNode, parent, adjList, answer, labels);
        dfs(0, 0, alist, ans, labels);

        //returning the answer vector which stores the actual result
        return ans;
    }
};
