func countSubTrees(n int, edges [][]int, labels string) []int {

    if len(edges) == 0 {
        return []int{}
    }

    graph := make(map[int][]int)
    for i:=0; i<n; i++ {
        graph[i] = make([]int, 0)
    }
    for _, edge := range edges {
        graph[edge[0]] = append(graph[edge[0]], edge[1])
        graph[edge[1]] = append(graph[edge[1]], edge[0])
    }
    ans := make([]int, n)
    for i := range ans {
        ans[i]++
    }
    dfs(graph, labels, &ans, 0, -1)
    return ans
}

func dfs(graph map[int][]int, labels string, ans *[]int, node, parent int) []int {

        //store the all the labels we can get in any subtree of parent node
        nodeCounts := make([]int, 26)
        nodeCounts[labels[node]-'a'] = 1

        for _, child := range graph[node] {
            if child == parent {
                continue
            }
            childCounts := dfs(graph, labels, ans, child, node)
            for i := range childCounts {
                nodeCounts[i] += childCounts[i]
            }
        }

        (*ans)[node] = nodeCounts[labels[node]-'a']
        return nodeCounts
}
