        m.clear();
        this->data=data;
        this->label=label;

    }
};
class Solution {
public:
    Tree* buildTree(vector<vector<int>>&adj,int src, vector<bool>&vis,string&labels){
        vis[src]=true;
        Tree* root=new Tree(src,labels[src]);
        for(auto it: adj[src]){
            if(!vis[it]) root->m[it]=buildTree(adj,it,vis,labels);
        }
        return root;
    }
    vector<int> dfs(Tree* root, vector<int>&ans){
        if(root==NULL) return{};
        vector<int> letter(26,0);
        for(auto it: root->m){
            auto x=dfs(it.second,ans);
            for(int i = 0; i < 26; i++){
                letter[i]+=x[i];
            }
        }
        ans[root->data]=letter[root->label-'a']+1;
        letter[root->label-'a']+=1;
        return letter;
    }
    vector<int> countSubTrees(int n, vector<vector<int>>& edges, string labels) {
        vector<vector<int>> adj(n);
        vector<bool> vis(n,false);
        for(int i = 0; i < edges.size(); i++){
            adj[edges[i][0]].push_back(edges[i][1]);
            adj[edges[i][1]].push_back(edges[i][0]);
        }
        vector<int> ans(n,0);
        Tree* root=buildTree(adj,0,vis,labels);
        auto x=dfs(root,ans);
        return ans;
    }
};
