# Code
```
function countSubTrees(n: number, edges: number[][], labels: string): number[] {
    const res = new Array(n).fill(1);

    const adj = new Map<number,number[]>();
    for(const [from,to] of edges)
    {
        if(adj.has(from))
            adj.get(from).push(to)
        else
            adj.set(from,[to])

        if(adj.has(to))
            adj.get(to).push(from)
        else
            adj.set(to,[from])
    }

    const vis = new Array<boolean>(n).fill(false);

    dfs(0,adj,labels,vis,res);

    return res;
};

function dfs(node:number,adj:Map<number,number[]>,labels:string,vis:boolean[],res:number[]):Map<string,number>
{

    vis[node] = true;

    const m = new Map<string,number>();
    m.set(labels[node],1);

    for(const v of adj.get(node))
    {
        if(!vis[v])
        {
            const temp = dfs(v,adj,labels,vis,res);

            for(const [key,value] of temp)
            {
                if(m.has(key))
                    m.set(key,m.get(key)+value)
                else
                    m.set(key,value)
            }
        }
    }

    res[node] = m.get(labels[node])

    return m;
}
```

