class Solution {
public:
    void sameLabel(int n,vector<vector<int>>& graph, vector<int>& ans, string labels, int source,vector<bool> parent,int pred){
        parent[source]=true;
        for(int i=0;i<graph[source].size();i++){
            if(graph[source][i]!=pred){
                sameLabel(n,graph,ans,labels,graph[source][i],parent,source);
            }
        }
        for(int i=0;i<parent.size();i++){
            if(parent[i]==true && labels[i]==labels[source]){
                ans[i]++;
            }
        }
        parent[source]=false;
        return;
    }
    vector<int> countSubTrees(int n, vector<vector<int>>& edges, string labels) {
        if(edges.size()==0){
            return {1};
        }
        vector<vector<int>> graph(n);
        vector<int> ans(n);
        vector<bool> parent(n,false);
        for(int i=0;i<edges.size();i++){
            graph[edges[i][0]].push_back(edges[i][1]);
            graph[edges[i][1]].push_back(edges[i][0]);
        }
        sameLabel(n,graph,ans,labels,0,parent,-1);
        return ans;
    }
};
