    def countSubTrees(self, n: int, edges: List[List[int]], labels: str) -> List[int]:
        E = defaultdict(set)
        for f,t in edges:
            E[f].add(t)
            E[t].add(f)
        N = {i:e for i,e in enumerate(labels)}
        R = [0]*n
        def r(ni):
            C = Counter()
            for e in E[ni]:
                E[e].remove(ni)
                C.update(r(e))
            C.update((N[ni]))
            R[ni] = C[N[ni]]
            return C
        r(0)
        return R

