    void dfs(map<int , list<int>>& graph , map<int,map<char,int>>& dp ,  string& s , vector<bool>& visited ,  int ptr  ){
        visited[ptr] = true;

        for(auto& i : graph[ptr]){
            if(!visited[i]){
                dfs(graph, dp, s, visited , i);
                for(auto& j : dp[i]){
                    dp[ptr][j.first] += j.second;
                }
            }

        }

        dp[ptr][s[ptr]]++;

    }

    vector<int> countSubTrees(int n, vector<vector<int>>& edges, string labels) {
        map<int , list<int>> graph;

        if(n == 1)
            return {1};

        for(auto& i : edges){
            graph[i[0]].push_back(i[1]);
            graph[i[1]].push_back(i[0]);
        }
        map<int , map<char,int>> dp;

        vector<bool> visited(n , false);

        dfs(graph, dp , labels , visited, 0);



        vector<int> ans(n);

        for(int i = 0 ; i < n ; i++){
           ans[i] = dp[i][labels[i]]; 
        //    cout << ans[i] << " "; 
        }

     return ans;



    }
};

