class Solution {
private:
    int maxSubArray(vector<int>& nums, int low, int high){
        if(low == high){
            return nums[low];
        }
        int leftMaxSumSubarray = INT_MIN, rightMaxSumSubarray = INT_MIN;

        int mid = (low + high) >> 1;
        int currSum = 0;
        for(int i=mid; i>=low; i--){
            currSum += nums[i];
            leftMaxSumSubarray = max(leftMaxSumSubarray, currSum);
