        int maxSumSubarray =  leftMaxSumSubarray +  rightMaxSumSubarray;
        int leftAnsSum = maxSubArray(nums, low, mid);
        int rightAnsSum = maxSubArray(nums, mid+1, high);

        return max(maxSumSubarray, max(leftAnsSum, rightAnsSum));
    }
