class Solution {
public:
    int maxSubArray(vector<int>& nums) {
        // meh – max sum ending at ith index
        int max = INT_MIN, meh = 0;
        for (int i=0; i<nums.size(); i++) {
            meh = meh + nums[i];
            if (meh > max) {
                max = meh;
            }
            if (meh < 0) {
                meh = 0;
            }
        }
        return max;
    }
};
```

