            int max_sum = A[0];
            dp[0] = A[0];

            for(int i=1; i<A.length; i++){
                dp[i] = Math.max(dp[i-1] + A[i], A[i]);
                max_sum = Math.max(dp[i], max_sum);
        	}

            return max_sum;	 
