class Solution {
public:
    int maxSubArray(vector<int>& nums) {
        int sum=0;
        int max_sum=0;
        for(int i=0;i<nums.size();i++){ 
            if(sum+nums[i]>0){
                sum+=nums[i];
                max_sum=max(max_sum,sum);
            }
            else{
                sum=0;
            }
        }
        if(max_sum==0){
            return *max_element(nums.begin(),nums.end());
        }
        return max_sum;
    }
};

