class Solution {
    public int findMin(int[] nums) {
        int left = 0, right = nums.length - 1;
        while (left < right) {
            if (nums[left] < nums[right]) { // Sorted as a whole
                return nums[left];
            }
            int mid = left + (right - left) / 2;
            if (nums[mid] > nums[right]) { // Unsorted right half
                left = mid + 1;
            } else { // Unsorted left half
                left++;
                right = mid;
            }
        }
        return nums[left];
    }
}
