public class Solution {
    public int FindMin(int[] nums) {
        var n = nums.Length;

        var left = 0;
        var right = n - 1;

        var globalMin = int.MaxValue;

        while (left <= right) {
            var mid = left + (right - left) / 2;
            if (nums[mid] < nums[right]) {
                globalMin = Math.Min(globalMin, nums[mid]);
                right = mid - 1;
            } else {
                globalMin = Math.Min(globalMin, nums[left]);
                left = mid + 1;
            }
        }

        return globalMin;
    }
}
```

