            if (nums[mid] >= nums[l]) {
                // The mini must be on the right side
                ans = min(ans, nums[l]); // Update ans
                l = mid + 1;
            } else {
                // The mini must be on the left side
                ans = min(ans, nums[mid]); // Update ans 
                r = mid - 1;
            }
        }

        return ans;
    }
};
