class Solution {
public:
    int findMin(vector<int>& nums) {
        //using binary search
        int low = 0;
        int high = nums.size()-1;
        //if the array is rotated n times
        if(nums[low] <= nums[high]) return nums[low];
        while(low <= high){
            int mid = (low+high)/2;
            // check on right element
            if(nums[mid]  > nums[mid+1])return nums[mid+1];
            //check on left element
            else if(nums[mid] < nums[mid-1]) return nums[mid];
            //discard the sorted array and shift low or high
            else if(nums[low]<= nums[mid]) low = mid+1;
            else if(nums[mid]<= nums[high]) high = mid-1;
        }
        return -1;
    }
};

