class Solution {
    public int findMin(int[] nums) {
        int min = min(nums);
        return nums[min];
    }
    public static int min(int[] nums){
        int start = 0;
        int end = nums.length - 1;
        while(start < end){
            int mid = start + (end - start) / 2;
            if(mid > 0 && nums[mid - 1] > nums[mid]){
                return mid;
            }
            else if(nums[start] <= nums[mid] && nums[mid] > nums[end]){
                start = mid + 1;
            }
            else{
                end = mid - 1;
            }
        }
        return start;
    }
}
