class Solution {
    public int lastStoneWeight(int[] stones) {
        int res=0;
        PriorityQueue<Integer> pq=new PriorityQueue<>(Collections.reverseOrder());
        for(int i: stones){
            pq.add(i);
        }
        while(!pq.isEmpty()){
            if(pq.size()==1){
                return pq.poll();
            }
            int f=pq.poll();
            int s=pq.poll();
            if(Math.abs(f-s)!=0){
                pq.add(Math.abs(f-s));
            }
        }
        return 0;
    }
}
