class Solution:
    def minSwaps(self, grid: List[List[int]]) -> int:
        n, step = len(grid), 0
        grid = list(reversed(grid))
        for target in reversed(range(1, n)):
            r = target
            while r >= 0:
                row = grid[r]
                if sum(row[-target:]) == 0: break
                r -= 1
            else:
                return -1
            while r != target:
                grid[r], grid[r+1] = grid[r+1], grid[r]
                step += 1
                r += 1
        return step
```

