class Solution:
    def minSwaps(self, grid: List[List[int]]) -> int:
        def count_trailing_zero(arr):
            idx = len(arr) - 1
            while idx >= 0 and arr[idx] == 0:
                idx -= 1
            return len(arr) - 1 - idx

        ranks = [count_trailing_zero(grid[i]) for i in range(len(grid))]
        swaps = 0

		# bubble sort
        for i in range(len(ranks)):
            j = i
            while j < len(ranks) and ranks[j] < len(ranks) - 1 - i:
                j += 1

			# grid is invalid because appropriate row replacement not found
            if j == len(ranks):
                return -1

            tmp = ranks.pop(j)
            ranks.insert(i, tmp)
            swaps += j - i

        return swaps
