class Solution {
public:
    int minSwaps(vector<vector<int>>& grid) {
        //brute force
        int res=0;
        int n=grid.size();
        for(int i=0;i<n;i++){
            //first row must have n-1 zeroes from the back;
            int counter=n-i-1;
            int j=n-1;
            int ctz=0;
            for(int k=j;k>i;k--){
                if(grid[i][k]==0){
                    ctz++;
                }
            }
            if(ctz==counter){
                continue;//this row is okay
            }
            //but if not we need to find the row which has counter number of zeroes
            int row=-1;
            for(int q=i+1;q<n;q++){
                int ctz1=0;
                for(int j=n-1;j>i;j--){
                    if(grid[q][j]==0){
                        ctz1++;
                    }
                }
                if(ctz1==counter){
                    row=q;
                    break;
                }
            }
            //cout<<row<<endl;
            if(row==-1){res=-1;break;}
            //else we have to bring this row i.e the qth row from the bottom to the top
            int swaps=row-i;
            while(swaps>0){
                for(int oo=n-1;oo>=0;oo--){
                    swap(grid[row][oo],grid[row-1][oo]);
                }
                row=row-1;
                swaps--;
                res++;
            }   
        }
        return res;
    }
};
```

