class Solution {
    public int minSwaps(int[][] grid) {
        int[] rows = new int[grid.length];


        for (int i = 0; i < grid.length; i++) {
            for (int j = grid.length-1; j>=0; j--) {
                if (grid[i][j] == 1)
                    break;

                rows[i]++;
            }

        }

        return sort(rows);
    }

    private int sort(int[] a){
        int steps = 0;
        int n = a.length-1;

        for (int i=0; i<=n; i++) {
            int j = i;
            while (j <= n && a[j] < n-i) {
                j++;
            }

            if (j == n+1) return -1;

            if (i<j && i<n) {
                int tmp = a[i];
                for(int k=i+1; k<=j; k++){
                    int tt = a[k];
                    a[k] = tmp;
                    tmp = tt;
                }

                a[i] = tmp;
            }

            steps += j-i;
        }
        return steps;
    }
}
