class Solution:
    def minSwaps(self, grid: List[List[int]]) -> int:
        res = 0
        for i, x in enumerate(grid):
            found = False
            for j in range(i, len(grid)):
                ok = True
                for k in range(i+1, len(grid[j])):
                    if grid[j][k] == 1:
                        ok = False
                        break
                if ok:
                    found = True
                    while j > i:
                        res += 1
                        grid[j], grid[j-1] = grid[j-1], grid[j]
                        j -= 1
                    break
            if not found: return -1
        return res
