const minSwaps = function (g) {
  const m = g.length
  const n = g[0].length

  function swapRows(r1,r2) {
    for (let C = 0; C < n; C++)
      [g[r1][C],g[r2][C]] = [g[r2][C],g[r1][C]]
  }

  function rowWithEnoughZerosAfterMainDiagonal(R2) {
    const zNeeded = n - (R2 + 1)
    for (let R = R2; R < m; R++) {
      const zc = g[R].slice(R2 + 1)
        .join('')
        .replaceAll('1','')
      if (zc.length >= zNeeded)
        return R
    }
    return -1
  }

  let cc = 0
  for (let R = 0; R < m; R++) {
    const rNeededToSwapTo = rowWithEnoughZerosAfterMainDiagonal(R)
    if (rNeededToSwapTo === -1)
      return -1
    if (R !== rNeededToSwapTo)
      for (let R2 = rNeededToSwapTo; R2 > R; R2--) {
        swapRows(R2,R2 - 1)
        cc += 1
      }
  }
  return cc
}
