class Solution {
public:
    int n;

    void swap(vector<vector<int>> &grid, int i1, int i2){
        for(int i=0; i<n; i++){
            int t=grid[i1][i];
            grid[i1][i]=grid[i2][i];
            grid[i2][i]=t;
        }
    }

    bool isValid(vector<vector<int>> &grid){
        for(int i=0; i<n; i++){
            for(int j=i+1; j<n; j++){
                if(grid[i][j]==1) return false;
            }
        }

        return true;
    }

    int minSwaps(vector<vector<int>>& grid) {
        n=grid[0].size();
        int ans=0;
        int n=grid.size();
        if(isValid(grid)) return 0;

        for(int i=0;i<n;i++){
            int counter=n-i-1;
            int ctz=0;
            for(int k=n-1;k>i;k--){
                if(grid[i][k]==0){
                    ctz++;
                }
            }

            if(ctz==counter) continue;

            int row=-1;
            for(int q=i+1; q<n; q++){
                int ctz1=0;
                for(int j=n-1;j>i;j--){
                    if(grid[q][j]==0){
                        ctz1++;
                    }
                }

                if(ctz1==counter){
                    row=q;
                    break;
                }
            }

            //cout<<row<<endl;
            if(row==-1){
                ans=-1;
                break;
            }

            int swaps=row-i;
            while(swaps>0){
                swap(grid, row, row-1);
                row=row-1;
                swaps--;
                ans++;
            }   
        }

        return ans;
    }
};
