func minSwaps(grid [][]int) int {
    n := len(grid)
    var list []int
    for i := 0; i < n; i++ {
        cnt := 0
        for j := n - 1; j >= 0; j-- {
            if grid[i][j] == 0 {
                cnt++
            } else {
                break
            }
        }
        list = append(list, cnt)
    }
    res := 0
    LOOP:
    for i := 0; i < n; i++ {
        if list[i] >= n - 1 - i {
            continue
        } else {
            for j := i + 1; j < n; j++ {
                if list[j] >= n - 1 - i {
                    for k := j - 1; k >= i; k-- {
                        list[k], list[k + 1] = list[k + 1], list[k]
                        res++
                    }
                    continue LOOP
                }
            }
            return -1
        }
    }
    return res
}
