var minSwaps = function(grid) {
    const n = grid.length;
    const lastIndex = n - 1;
    const mostRight = new Array(n);
    let result = 0;
    let tmp;

    for (let i = 0; i < n; ++i) {
        for (let j = lastIndex; j >= 0; --j) {
            if (grid[i][j] === 1) {
                mostRight[i] = j;
                break;
            }
        }
        if (mostRight[i] === undefined) mostRight[i] = -1;
    }

    for (let i = 0; i < lastIndex; ++i) {
        if (mostRight[i] <= i) continue;

        for (let j = i + 1; j < n; ++j) {
            if (mostRight[j] <= i) {
                while (j !== i) {
                    tmp = mostRight[j];
                    mostRight[j] = mostRight[j - 1];
                    mostRight[j - 1] = tmp;
                    ++result;
                    --j;
                }
                break;
            }
            if (j === lastIndex) return -1;
        }
    }

    return result;
};
