var minSwaps = function(grid) {
    const nums = grid.map(row => row.lastIndexOf(1))
    const len = nums.length


    let result = 0
    for (let fromIndex = 0; fromIndex < len; fromIndex++) {
        let bestIndex = -1
        let bestVal = Infinity

        for (let i = fromIndex; i < len; i++) {
            const val = nums[i]
            if (val <= fromIndex) {
                bestIndex = i
                bestVal = val
                break
            }
        }
        if (bestIndex === -1)   return -1

        for (let i = bestIndex; i > fromIndex; i--) {
            nums[i] = nums[-1 + i]
        }
        nums[fromIndex] = bestVal

        result += bestIndex - fromIndex
    }


    return result
};
