class Solution(object):
    def minSwaps(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """
        n = len(grid)
        zeros = []
        need = n - 1
        ans = 0
        for i in range(n):
            cur_zeros = n
            for j in range(n - 1, -1, -1):
                if grid[i][j] == 1:
                    cur_zeros = n - j - 1
                    break
            zeros.append(cur_zeros)
        while zeros:
            for i in range(len(zeros)):
                if zeros[i] >= need:
                    need -= 1
                    break
            else:
                return -1
            ans += i
            zeros.pop(i)
        return ans
