const minSwaps = (grid) => {
    let n = grid.length;
    let a = new Array(n).fill(0);
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
            if (grid[i][n - 1 - j] != 0) break;
            a[i]++;
        }
    }
    let res = 0;
    top:
        for (let i = 0; i < n; i++) {
            for (let j = i; j < n; j++) {
                if (a[j] >= n - i - 1) {
                    for (let k = j - 1; k >= i; k--) {
                        [a[k], a[k + 1]] = [a[k + 1], a[k]];  // swap
                        res++;
                    }
                    continue top;
                }
            }
            return -1;
        }
    return res;
};

