class Solution:
    def minSwaps(self, grid: List[List[int]]) -> int:
        n = len(grid)
        def lastOne(row):
            return next((i for i in range(n-1, -1, -1) if row[i]), -1)
        rightZeros = [n - 1 - lastOne(row) for row in grid]
        for i, rz in enumerate(sorted(rightZeros)):
            if rz < i:
                return -1
        ans = 0    
        for i in range(n):
            target = n + ~i
            if rightZeros[i] < target:
                j = i + 1
                while rightZeros[j] < target:
                    j += 1
                ans += j - i     
                rightZeros[i:j+1] = [rightZeros[j]] + rightZeros[i:j]
        return ans          
