class Solution {
public:
    int minSwaps(vector<vector<int>>& grid) {
        int n=grid.size();
        unordered_map<int,int>mp,mp2;
        for(int i=0;i<n;i++)
        {
            int c1=0;
            for(int j=n-1;j>=0;j--)
            {
                if(grid[i][j]==0)
                    c1++;
                else
                    break;
            }
            mp[i]=c1;
        }
        int ans=0;
        for(int i=0;i<n;i++)
        {
            int x=mp[i];
            int l=(n-i-1);
            int j=i;
            while(x<l && j<n-1)
            {
                ans++;
                x=mp[++j];
                if(j==(n-1) && x<l)
                    return -1;
            }
            for(int l=j;l>=i+1;l--)
                mp[l]=mp[l-1];
            mp[i]=l;
        }
        return ans;
    }
};
