class Solution {
    public int minSwaps(int[][] grid) {
        int n = grid.length;
        int[] cntZeros = new int[n];

        for (int i = 0; i < n; i++) {
            int cnt = 0;
            for (int j = n - 1; j >= 0; j--) {
                if (grid[i][j] != 0) {
                    break;
                }
                cnt++;
            }
            cntZeros[i] = cnt;
        }
        int res = 0;
        int target = n - 1;
        for (int i = 0; i < n; i++) {
            int j;
            for (j = i; j < n; j++) {
                if (target <= cntZeros[j]) {
                    break;
                }
            }
            if (j == n) {
                return -1;
            }
            res += j - i;
            moveToFront(cntZeros, i, j);    
            target--;
        }
        return res;
    }

    private void moveToFront(int[] cntZeros, int i, int j) {
        int temp = cntZeros[j];
        for (int k = j; k > i; k--) {
            cntZeros[k] = cntZeros[k - 1];
        }
        cntZeros[i] = temp;
    }
}
```

