class Solution {
    public int minSwaps(int[][] grid) {
        int[] zeroes = calculateZeroesFromRight(grid);
        int steps = countSortSteps(zeroes);
        return steps;
    }

    private int[] calculateZeroesFromRight(int[][] grid) {
        int n = grid.length;
        int[] zeroes = new int[n];

        for (int i = 0; i < n; i++) {
            int j;
            for (j = n - 1; j >= 0 && grid[i][j] == 0; j--);

            zeroes[i] = n - j - 1;
        }

        return zeroes;
    }

    private int countSortSteps(int[] arr) {
        int n = arr.length;
        int steps = 0;

        for (int i = 0; i < n; i++) {
            int target = n - i - 1;
            int j = i;

            while (j < n && arr[j] < target) {
                j++;
            }

            if(j >= n) {
                return -1;
            }

            steps += j - i;
            while (j > i) {
                swap(arr, j, j - 1);
                j--;
            }
        }

        return steps;
    }

    private void swap(int[] arr, int i, int j) {
        int tmp = arr[i];
        arr[i] = arr[j];
        arr[j] = tmp;
    }
}
