            if(res[i] >= t) continue;
            int j = i + 1;
            while(j < g.length && res[j] < t) j++;
            if(j == g.length) return -1;
            ans += j-i;
            while(j > i) {
                int z = res[j];
                res[j] = res[j-1];
                res[j-1] = z;
                j--;
            }
        }
        return ans;
    }
