class Solution {
    public int minSwaps(int[][] grid) {
        int[] counts = new int[grid.length];
        int n = grid.length;
        int steps = 0;
        // count contiguous zero from right to left
        for (int i = 0; i < n; i++) {
            for (int j = n-1; j >= 0; j--) {
                if (grid[i][j] == 0) {
                    counts[i]++;
                } else {
                    break;
                }
            }
        }
        for (int i = 0; i < n; i++) {
            if (counts[i] < n - 1 - i) {
                boolean isValid = false;
                for (int j = i + 1; j < n; j++) {
                    if (counts[j] >= n-1-i) {
                        steps+= move(counts,i,j);
                        isValid = true;
                        break;	
                    }
                }
                if (!isValid) return -1;
            } 
        }
        return steps;
    }
    private int move(int[] count, int dst, int src) {
        int m = 0;
        for (int i = src; i > dst; i--) {
            swap(count, i, i-1);
            m++;
        }
        return m;
    }
    private void swap(int[] ary, int i, int j) {
        int temp = ary[i];
        ary[i] = ary[j];
        ary[j] = temp;
    }
}
