public boolean canConvertString(String s, String t, int k) {
        if(s.length()!=t.length())
            return false;
        Map<Integer,Integer> seen=new HashMap<>();
        for(int i=0;i<s.length();i++){
            int d=(t.charAt(i)-'a')-(s.charAt(i)-'a');
            if(d==0)
                continue;
            if(d<0)
                d+=26;
            if(d>k)
                return false;
            if(seen.containsKey(d)){
                int lastShift=seen.get(d);
                if(26+lastShift<=k)
                    seen.put(d,lastShift+26);
                else
                    return false;
            } else
                seen.put(d,d);
        }
        return true;
    }
