    public boolean canConvertString(String s, String t, int k) {
        if (s.length() != t.length()) {
            return false;
        }
        int round = k / 26;
        int remaining = k % 26;
        int[] record = new int[26];
        for (int i = 0; i < 26; i++) {
            if (i < remaining) {
                record[i] += 1;
            }
            record[i] += round;
        }
        for (int i = 0; i < s.length(); i++) {
            char c1 = s.charAt(i);
            char c2 = t.charAt(i);
            if (c1 == c2) continue;
            int index = (c2 - c1 + 26) % 26 - 1;
            if (record[index] > 0) {
                record[index] -= 1;
            } else {
                return false;
            }
        }
        return true;
    }
}

