    def canConvertString(self, s: str, t: str, K: int) -> bool:
        if len(s) != len(t):
            return False
        need, needs = {}, 0
        # ✅ add needs 
        for i in range(len(s)):
            diff = (ord(t[i]) - ord(s[i]) + 26) % 26
            if diff:
                if not diff in need:
                    need[diff] = 0
                need[diff] += 1
                needs += 1
        # 🚫 del needs
        for i in range(1, K + 1):
            if not needs:
                break
            diff = i % 26
            if diff in need and need[diff]:
                need[diff] -= 1
                needs -= 1
        return not needs # 🎯 no needs
