class Solution {
public:
    bool canConvertString(string s, string t, int k) {
        if(s.length()!=t.length()){
            return false;
        }

        vector<int> tracker(26, 0);
        for(int i=0;i<s.length();i++){
            int diff;
            if(t[i]>s[i]){
                diff = (t[i]-'a')-(s[i]-'a');
            }
            else{
                diff = ('z'-s[i]) + (t[i]-'a') + 1;
            }
            if(diff==0 || diff==26){
                continue;
