            if(diff>k){
                return false;
            }
            if(tracker[diff]!=0){
                tracker[diff]+=26;
                if(tracker[diff]>k){
                    return false;
                }
            }
            else{
                tracker[diff] += diff;
            }
        }
        return true;
    }
};
