        for(int i = 0; i < sl; i++) {
            if(s[i] == t[i]) continue;
            int diff = t[i]-s[i];
            if(diff < 0) diff += 26;
            if(diff > k || m[diff] <= 0) return false;
            m[diff]--;
        }
        return true;
    }
};
