public class Solution {
    public bool CanConvertString(string s, string t, int k) {
        if (s.Length != t.Length)
            return false;

        int[] moves = Enumerable
            .Range(0, 26)
            .Select(i => k / 26 + (i <= k % 26 ? 1 : 0))
            .ToArray();

