class Solution {
public:
    bool canConvertString(string s, string t, int k) {
        if(s.size()!=t.size()) return false;

        vector<int> diffCount(26,0);
        for(int i=0;i<s.size();++i) diffCount[(t[i]-s[i]+26)%26]++;

        int repeat = k/26, remain = k%26;
        vector<int> diffCountAvailable(26,repeat);
        for(int i=1;i<=remain;++i) diffCountAvailable[i]++;

        for(int i=1;i<diffCount.size();++i) 
            if(diffCount[i]>diffCountAvailable[i]) return false;

        return true;
    }
};
