                v[diff].first = 1;
                maxK = max(maxK, diff);
            } else {
                v[diff].second += 1;
                diff = 26*v[diff].second + diff;
                maxK = max(maxK, diff);
            }
        }
        return (maxK>k)?false:true;
    }
};

