        if (s.length() != t.length()) return false;

        var map = new HashMap<Integer, Integer>();

        for (int i = 0; i < s.length(); i++) {
            if (t.charAt(i) == s.charAt(i)) continue;
            else if (t.charAt(i) - s.charAt(i) > 0) {
                var c = t.charAt(i) - s.charAt(i);
                map.put(c, map.getOrDefault(c, 0) + 1);
            } else {
                var c = 26 - s.charAt(i) + t.charAt(i);
                map.put(c, map.getOrDefault(c, 0) + 1);
            }
        }

        var used = new HashSet<Integer>();
        for (var it : map.entrySet()) {
            var next = it.getKey();

            for (int i = it.getValue(); i > 0; i--) {
                while(used.contains(next)) next += 26;

                if (next > k) return false;
                else used.add(next);
            }   
        }

        return true;
    }
