func canConvertString(s string, t string, k int) bool {
    if len(s) != len(t) {
        return false
    }

    cache := make([]int, 26) // how many ops needed
    for i := range s {
        if s[i] != t[i] {  // needs shifting
            opsNeeded := int(t[i] + 26 - s[i]) % 26
            turn := opsNeeded + 26 * cache[opsNeeded]
            cache[opsNeeded]++

            if turn > k {
                return false
            }
        }
    }

    return true
}
