var canConvertString = function(s, t, k) {
    let numMoves = 0;
    let shiftCountMap = {};
    let leastNumMoves = 0;
    let getMoves = function(from, to) {
      let result = to.charCodeAt(0) - from.charCodeAt(0);
      return result < 0 ? 26 + result : result;
    }

    if(s.length !== t.length) {
      return false;
    }

    for(let i = 0; i < s.length; i++) {
      numMoves = getMoves(s.charAt(i), t.charAt(i));
      shiftCountMap[numMoves] = !!shiftCountMap[numMoves] ? shiftCountMap[numMoves] + 1 : 1;
      let temp = (26 * (shiftCountMap[numMoves] - 1)) + numMoves;
      if(leastNumMoves < temp && numMoves !== 0){
        leastNumMoves = temp;
      }
    }
    return k >= leastNumMoves;
};
```

