class Solution {
    public int minInsertions(String s) {
        Stack<Character>stk=new Stack<>();
        int cnt=0;
        char c;
        for(int i=0;i<s.length();i++){
            c=s.charAt(i);
            if(c=='(') // if we get one '(' we simply insert it into the stack.
                stk.push(c);
            else{
                // if we get a ')',
                if(i<s.length()-1&&s.charAt(i+1)==')'){ // we check if the next(if present) character is also a ')'. 

                    // if stack is empty then one '(' will be required for the found '))', else we just matched a '(' with '))'.
                    if(stk.empty()) 
                        cnt++;
                    else    
                        stk.pop();

                    i++; // since we accessed the next element as well.
                }
                else{
                    // if the next element is not present or the next element is not a ')', then we obviously need a ')'. 
                    // Then if stack is empty, one '(' will be required to balance '))', else we got a '(' for '))'.                    
                    if(stk.empty()) 
                        cnt+=2; 
                    else{
                        stk.pop();
                        cnt++;
                    }
                }
            }
        }
        cnt+=stk.size()*2; // for each remaining '(', we require two '))'.
        return cnt;
    }
}
